pragma solidity ^0.5.16;

interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

contract EVChargingPayment {
    address public tokenAddress;
    address public owner;

    event PaymentMade(address indexed payer, uint256 amount, string stationId, string vehicleId);

    constructor(address _tokenAddress) public {
        tokenAddress = _tokenAddress;  // Use your deployed WFO token address here
        owner = msg.sender;
    }

    function payCharging(string memory stationId, string memory vehicleId, uint256 amount) public {
        require(IERC20(tokenAddress).transferFrom(msg.sender, owner, amount), "Payment failed");
        emit PaymentMade(msg.sender, amount, stationId, vehicleId);
    }
}
